command:
const modifiers = {
  strength: 0,
  constitution: 0,
  dexterity: 0,
  charisma: 0,
  intelligence: 0,
  strike: -1,
  insight: 0,
  speed: 0,
  defense: 3,
  will: 0,
  health: 0,
  power: 0
};

const tokens = canvas.tokens.controlled;
if (tokens.length === 0) {
  ui.notifications.warn("Select tokens first!");
} else {
  for (const token of tokens) {
    if (token.actor) {
      await token.document.setFlag("sdkdite", "regionModifiers", {
        regionId: "cover",
        modifiers: modifiers
      });
      token.actor.prepareData();
      if (token.actor.sheet?.rendered) token.actor.sheet.render(false);
    }
  }
  ChatMessage.create({
    content: `<div class="sdkdite region-entry"><h3>Cover Position</h3><p><strong>Tokens:</strong> ${tokens.map(t => t.name).join(", ")}</p><p class="modifiers">+3 Defense, -1 Strike</p></div>`,
    speaker: { alias: "Region Effect" }
  });
  ui.notifications.info(`Applied Cover to ${tokens.length} token(s)`);
}
